import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import SearchBar from './components/SearchBar';
import MovieList from './components/MovieList';
import MovieDetails from './components/MovieDetails';
import LoadingSpinner from './components/LoadingSpinner';

const App: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async (query: string) => {
    try {
      setIsLoading(true);
      setError(null);
      setSearchQuery(query);

      const url = `https://api.themoviedb.org/3/search/movie?api_key=336ce50989c7da4ec846349adc27407a&query=${query}`;
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }


      // ðŸ‘‰ Add your API call here
      // await searchMovies(query);

    } catch (error) {
      setError('Failed to fetch movies. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Router>
      <div className="App">

        //the hero section always available showing the top part
        <div className="hero-section">
          <h1>Welcome to MovieHub</h1>
          <SearchBar onSearch={handleSearch} />
        </div>

        //error message
        {error && <div className="error-message">{error}</div>}

        //the spinner loading , showing its processing
        {isLoading && <LoadingSpinner />}


         //main content of the movies
        <main className="main-content">
          <Routes>
            <Route path="/" element={<MovieList />} />
            <Route path="/movie/:id" element={<MovieDetails />} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
